#search_bar {
position: absolute;
top: 285px;
width: 100%;
}

.TabsPanel.panel {
  padding-top: 290px;

}

.Bookmarks.panel::before {
  content: 'Bookmarks';
}

.Bookmarks {
  font-weight: 500;
}

/*
.Tab {
  border-right: 2px solid transparent;
}
.Tab[data-active="true"] {
  border-right: 2px solid #a86595;
}
*/

.Tab .ctx {
  display: none;
}

.Tab .title {
  font-size: 13px;
  font-weight: 500 !important;
}

.Tab .close > svg {
  fill: var(--tabs-activated-fg);
}

.Tab .close:hover > svg {
  fill: var(--tabs-activated-fg);
  background-color: rgba(255,255,255,0.1);
  border-radius: 2px;
}

.Tab[data-discarded="true"] {
  color: var(--tabs-fg) !important;
  font-style: italic;
}

.Tab[data-discarded="true"] .t-box {
  opacity: 1 !important;
}

.panel-btn {
  border-left: 0 solid transparent;
  border-width: 0 2px;
}

.panel-btn > svg {
 --container-fg: #6272a4;
 --nav-btn-fg:  #6272a4;
 width: 24px !important;
 height: 24px !important;
}

/*
.panel-btn:hover {
  background-color: var(--tabs-bg-hover) !important;
}
*/

.panel-btn:hover > svg {
 --container-fg: var(--tabs-fg-hover);
 --nav-btn-fg:  var(--tabs-fg-hover);
}

.panel-btn[data-active="true"] {
  background-color: #43465a !important;
  border-left-color: #a86595;
}

.panel-btn[data-active="true"] > svg {
 --container-fg: var(--tabs-activated-fg);
 --nav-btn-fg:  var(--tabs-activated-fg);
}

.NavigationBar {
  background-color: #3a3f4e;
  box-shadow: none !important;
}

.TabsPanel .container, .PinnedDock {
  box-shadow: none !important;
}


/* OLD STYLES
#root.root {--tabs-activated-fg: rgb(255, 255, 255);}
#root.root {--frame-bg: rgb(0, 251, 252);}
#root.root {--frame-fg: rgb(70, 72, 83);}
#root.root {--toolbar-fg: rgb(70, 72, 83);}
#root.root {
    --tabs-height: 42px;
}

.Tab {
    margin-left: 5px !important;
    width: 92vw !important;
}


.Sidebar {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

.TabsPanel {
    margin-top: 0px !important;
}

.container, .PinnedTabsBar {
    margin: 0px !important;
    max-width: 150px;
}

.NavigationBar {
   # margin: 50px 5px 12px 13px !important;
    margin: 8px 5px 2px 8px !important;
    width: auto !important;
    border: none !important;
    background-color: transparent !important;
    box-shadow: none !important;
z-index: 9002 !important;
}

.NavigationBar:after {
    position: absolute;
    content: '';
    border-bottom: 1px solid var(--tabs-bg-hover);
    width: 110%;
    transform: translateX(-50%);
    bottom: 100px;
    left: 50%;
}


.NavigationBar .nav-item[data-active=true] {
		box-shadow: none !important;
    background-color: var(--tabs-activated-bg) !important;
}

.NavigationBar .nav-item .audio svg {
    padding: 2.5px !important;
    height: 12px !important;
    width: 12px !important;
    background: var(--frame-bg) !important;
    border-radius: 50%;
		box-shadow: rgba(0, 0, 0, 0.12) 0px 1px 3px, rgba(0, 0, 0, 0.24) 0px 1px 2px;
    margin-left: 0px !important;
}

@media (prefers-color-scheme:light) {
    #root {
        --frame-bg: white !important;
        --toolbar-bg: white !important;
        --popup-bg: var(--frame-bg) !important;
        --ctx-menu-bg: var(--frame-bg) !important;
        --frame-fg: #000 !important;
        --toolbar-fg: var(--frame-fg) !important;
        --popup-fg: var(--frame-fg) !important;
        --tabs-fg: #323232 !important;
        --tabs-bg-hover: #dedddaff !important;
        --tabs-activated-bg: #f0f0f0 !important;
        --btn-fg: #555 !important;
        --nav-btn-fg: var(--btn-fg) !important;
        --border-color: rgba(0,0,0,0.1);
        --search-border: red !important;
    }
}

@media (prefers-color-scheme:dark) {
    #root {
        --frame-bg: #181818 !important;
        --toolbar-bg: var(--frame-bg) !important;
        --popup-bg: var(--frame-bg) !important;
        --ctx-menu-bg: var(--frame-bg) !important;
        --frame-fg: #f2f2f2 !important;
        --toolbar-fg: var(--frame-fg) !important;
        --popup-fg: var(--frame-fg) !important;
        --tabs-bg-hover: #323232!important;
        --tabs-activated-bg: #363636!important;
        --btn-fg: #fff !important;
        --nav-btn-fg: var(--btn-fg) !important;
        --border-color: #242424 !important;
        --border: 5px solid rgba(86, 86, 86, 0.26) !important;
    }
}



.Tab .fav, .BookmarksPanel .fav, .HistoryPanel .fav {
    margin-right: 7px !important;
}

.Tab .audio svg {
    padding: 2.5px !important;
    height: 12px !important;
    width: 12px !important;
    background: var(--frame-bg) !important;
    border-radius: 50%;
    box-shadow: 1px 1px 5px rgba(0,0,0,0.25);
    margin-left: -15px !important;
		margin-top:5px !important;
}

.Tab[data-audible] .t-box, .Tab[data-muted] .t-box {
    transform: none !important;
}

@media screen and (max-width: 60px) {

    #root {
        --tabs-indent: 0px !important;
        --bookmarks-indent: 0px !important;
    }

    .Tab[data-lvl] , .PinnedTab[data-lvl] {
        max-width: 34px;
        float: left;
    }

    .container {
        margin: 0px !important;
        max-width: 50px;
    }

    .panel-btn[data-active="true"] {
        display: flex !important;
        position: absolute !important;
        background: none !important;
    }

    .NavigationBar {
        background-color: transparent !important;
        box-shadow: none !important;
        border-bottom: 1px !important;
    }

    .NavigationBar .nav-item {
        visibility: hidden
    }

    .NavigationBar  .nav-item[data-active=true] {
				left:-0.5px !important;
				position:absolute !important;
        visibility: visible;
        background-color: transparent !important;
        box-shadow: none !important;
        padding-left: 1px !important;
    }

    .NavigationBar  .nav-item[data-active=false] {
				left:-0.5px !important;
				position:absolute !important;
        visibility: visible;
        background-color: transparent !important;
        box-shadow: none !important;
        padding-left: 1px !important;
    }
}

.TabsPanel .new-tab-btns {
    #margin-bottom: 35px !important;
    padding-top: 5px !important;
		margin-top: 5px !important;
}

.BookmarksPanel , .HistoryPanel {
    margin-left: 15px !important;
    padding-bottom: 0px !important;
    padding-right: 20px !important;
}

.PinnedTabsBar:after {
    bottom: 4px;
    left: 0px;
    width: 300px !important;
    height: 1px;
    background-color: var(--tabs-bg-hover);
    opacity: 0.8;
}

   .Tab[data-active=true] .body:before {
		box-shadow: rgba(0, 0, 0, 0.1) 1.8px 1.5px 1px -1px, rgba(0, 0, 0, 0.06) 0px 2px 4px -1px !important;
   }

