sidebarCSS:
#root.root {--frame-bg: rgba(250, 250, 250,0);}
#root.root {
	--search-border-radius: 4px;
	--search-font-size: 0.8rem;
	--search-height: 28px;
	--ctx-menu-min-width: 200px;
	flex-direction: column-reverse;
background-color: transparent !important;
}

#search_bar {
	top: 5px;
	width: 100%;
background-color: transparent !important;
}


#search_bar {
	position: absolute;

	width: 90%;
	margin-left: 5%
}



.Bookmarks.panel:before {
	content: "Bookmarks"
}

.Bookmarks {
	font-weight: 500
}

.Tab .ctx {
	display: none
}

.Tab .title {
	font-size: 13px;
	font-weight: 500!important
}

.Tab .close:hover>svg,.Tab .close>svg {
	fill: var(--tabs-activated-fg)
}

.Tab .close:hover>svg {
	background-color: hsla(0,0%,100%,.1);
	border-radius: 2px
}

.Tab[data-discarded=true] {
	color: var(--tabs-fg)!important;
	font-style: italic
}

.Tab[data-discarded=true] .t-box {
	opacity: 1!important
}

.panel-btn {
	border-left: 2px solid transparent;
	border-bottom-width: 0;
	border-right-width: 2px;
	border-top-width: 0
}

.panel-btn>svg {
	--container-fg: #6272a4;
	--nav-btn-fg: #6272a4;
	width: 24px!important;
	height: 24px!important
}

.panel-btn:hover>svg {
	--container-fg: var(--tabs-fg-hover);
	--nav-btn-fg: var(--tabs-fg-hover)
}

.panel-btn[data-active=true] {
	background-color: #43465a!important;
	border-left-color: #a86595
}

.panel-btn[data-active=true]>svg {
	--container-fg: var(--tabs-activated-fg);
	--nav-btn-fg: var(--tabs-activated-fg)
}

.NavigationBar,.PinnedDock,.TabsPanel .container {
	box-shadow: none!important
}

#root.root.Sidebar{
	background-color: transparent !important;
}

